mapper.insert:
 INSERT INTO tbl_member(   USERID 
					       , USERPASS 
					       , USERNAME 	)
               VALUES(    #{userId} 
              		   , #{userPass}
              		   , #{userName})

mapper.select:
SELECT USERID, USERPASS 
  FROM tbl_member
 WHERE USERID = #{userId}
   AND USERPASS = #{userPass}

mapper.update:
UPDATE tbl_member SET 
USERPASS = #{userPass},
USERNAME = #{userName}
WHERE USERID = #{userId}

mapper.delete:
DELETE FROM tbl_member
WHERE USERID = #{userId}
  AND USERPASS = #{userPass}

mapper.select:
SELECT COUNT(*) FROM tbl_member
WHERE USERID = #{userId}
  AND USERPASS = #{userPass}
